import React, { useState, useContext, useEffect } from 'react';
import {
  Typography,
  Paper,
  Box,
  Grid,
  TextField,
  Button,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Switch,
  FormControl,
  FormControlLabel,
  Select,
  MenuItem,
  Alert,
  Tabs,
  Tab,
  InputAdornment,
  Card,
  CardContent,
  CardHeader,
  Snackbar,
} from '@mui/material';
import {
  Save as SaveIcon,
  Refresh as RefreshIcon,
  Security as SecurityIcon,
  Storage as StorageIcon,
  Notifications as NotificationsIcon,
  AccountCircle as AccountCircleIcon,
  Settings as SettingsIcon,
  CloudDownload as CloudDownloadIcon,
} from '@mui/icons-material';
import { ColorModeContext } from '../App';

// Tab panel component
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`settings-tabpanel-${index}`}
      aria-labelledby={`settings-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

const Settings = () => {
  const colorMode = useContext(ColorModeContext);
  const [tabValue, setTabValue] = useState(0);
  const [settings, setSettings] = useState({
    // General settings
    darkMode: colorMode.mode === 'dark',
    showNotifications: true,

    // Maven settings
    mavenPath: '/usr/bin/mvn',
    mavenRepoPath: '~/.m2/repository',

    // License settings
    licenseChecking: true,
    restrictedLicenses: ['GPL', 'AGPL'],

    // Vulnerability settings
    vulnerabilityChecking: true,
    vulnerabilityCheckDelay: 0,

    // Chart settings
    defaultChartType: 'pie',

    // Cache settings
    cacheEnabled: true,
    cacheDuration: 24,
  });

  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success'
  });

  // Effect to sync the dark mode state when it changes externally
  useEffect(() => {
    setSettings(prev => ({
      ...prev,
      darkMode: colorMode.mode === 'dark'
    }));
  }, [colorMode.mode]);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const handleSettingChange = (setting, value) => {
    setSettings({
      ...settings,
      [setting]: value
    });

    // Special handling for dark mode to update application theme
    if (setting === 'darkMode' && value !== (colorMode.mode === 'dark')) {
      colorMode.toggleColorMode();
    }
  };

  const handleSave = () => {
    // Here you would implement the API call to save settings to the backend
    console.log('Saving settings:', settings);

    // Show success message
    setSnackbar({
      open: true,
      message: 'Settings saved successfully',
      severity: 'success'
    });
  };

  const handleReset = () => {
    // Reset to default values (would be fetched from backend in a real app)
    setSettings({
      darkMode: false,
      showNotifications: true,
      mavenPath: '/usr/bin/mvn',
      mavenRepoPath: '~/.m2/repository',
      licenseChecking: true,
      restrictedLicenses: ['GPL', 'AGPL'],
      vulnerabilityChecking: true,
      vulnerabilityCheckDelay: 0,
      defaultChartType: 'pie',
      cacheEnabled: true,
      cacheDuration: 24,
    });

    // Show info message
    setSnackbar({
      open: true,
      message: 'Settings reset to defaults',
      severity: 'info'
    });
  };

  const handleCloseSnackbar = () => {
    setSnackbar({
      ...snackbar,
      open: false
    });
  };

  return (
    <>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <SettingsIcon sx={{ mr: 1 }} />
        <Typography variant="h4" component="h1">
          Settings
        </Typography>
      </Box>

      <Paper sx={{ mb: 4 }}>
        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <Tabs
            value={tabValue}
            onChange={handleTabChange}
            aria-label="settings tabs"
            variant="scrollable"
            scrollButtons="auto"
          >
            <Tab label="General" icon={<SettingsIcon />} iconPosition="start" />
            <Tab label="Maven Configuration" icon={<StorageIcon />} iconPosition="start" />
            <Tab label="License Checking" icon={<AccountCircleIcon />} iconPosition="start" />
            <Tab label="Vulnerability Scanning" icon={<SecurityIcon />} iconPosition="start" />
            <Tab label="Updates & Cache" icon={<CloudDownloadIcon />} iconPosition="start" />
          </Tabs>
        </Box>

        {/* General Settings */}
        <TabPanel value={tabValue} index={0}>
          <Typography variant="h6" gutterBottom>
            Application Settings
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <List>
                    <ListItem>
                      <ListItemIcon>
                        <SettingsIcon />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Dark Mode" 
                        secondary="Enable dark theme for the application" 
                      />
                      <Switch
                        edge="end"
                        checked={settings.darkMode}
                        onChange={(e) => handleSettingChange('darkMode', e.target.checked)}
                      />
                    </ListItem>
                    <Divider />
                    <ListItem>
                      <ListItemIcon>
                        <NotificationsIcon />
                      </ListItemIcon>
                      <ListItemText 
                        primary="Notifications" 
                        secondary="Show notifications for completed analyses" 
                      />
                      <Switch
                        edge="end"
                        checked={settings.showNotifications}
                        onChange={(e) => handleSettingChange('showNotifications', e.target.checked)}
                      />
                    </ListItem>
                  </List>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </TabPanel>

        {/* Maven Configuration */}
        <TabPanel value={tabValue} index={1}>
          <Typography variant="h6" gutterBottom>
            Maven Configuration
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <Box sx={{ mb: 3 }}>
                    <TextField
                      fullWidth
                      label="Maven Path"
                      variant="outlined"
                      value={settings.mavenPath}
                      onChange={(e) => handleSettingChange('mavenPath', e.target.value)}
                      helperText="Path to Maven executable (mvn)"
                      margin="normal"
                    />

                    <TextField
                      fullWidth
                      label="Maven Repository Path"
                      variant="outlined"
                      value={settings.mavenRepoPath}
                      onChange={(e) => handleSettingChange('mavenRepoPath', e.target.value)}
                      helperText="Path to local Maven repository"
                      margin="normal"
                    />
                  </Box>

                  <Alert severity="info" sx={{ mt: 2 }}>
                    Configure these settings only if you want to use a custom Maven installation.
                    If left blank, the system will use the default Maven installation.
                  </Alert>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </TabPanel>

        {/* License Settings */}
        <TabPanel value={tabValue} index={2}>
          <Typography variant="h6" gutterBottom>
            License Checking Configuration
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <Box sx={{ mb: 3 }}>
                    <FormControlLabel
                      control={
                        <Switch
                          checked={settings.licenseChecking}
                          onChange={(e) => handleSettingChange('licenseChecking', e.target.checked)}
                        />
                      }
                      label="Enable License Checking"
                    />

                    <Typography variant="body2" color="text.secondary" sx={{ mt: 1, mb: 2 }}>
                      When enabled, the system will check dependency licenses and flag potentially problematic licenses.
                    </Typography>

                    <TextField
                      fullWidth
                      label="Restricted Licenses"
                      variant="outlined"
                      value={settings.restrictedLicenses.join(', ')}
                      onChange={(e) => handleSettingChange('restrictedLicenses', e.target.value.split(',').map(item => item.trim()))}
                      helperText="Comma-separated list of license types to flag as restricted"
                      margin="normal"
                      disabled={!settings.licenseChecking}
                    />
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </TabPanel>

        {/* Vulnerability Settings */}
        <TabPanel value={tabValue} index={3}>
          <Typography variant="h6" gutterBottom>
            Vulnerability Scanning Configuration
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <Box sx={{ mb: 3 }}>
                    <FormControlLabel
                      control={
                        <Switch
                          checked={settings.vulnerabilityChecking}
                          onChange={(e) => handleSettingChange('vulnerabilityChecking', e.target.checked)}
                        />
                      }
                      label="Enable Vulnerability Scanning"
                    />

                    <Typography variant="body2" color="text.secondary" sx={{ mt: 1, mb: 2 }}>
                      When enabled, the system will check for known vulnerabilities in dependencies.
                    </Typography>

                    <TextField
                      fullWidth
                      label="Vulnerability Check Delay (seconds)"
                      variant="outlined"
                      type="number"
                      value={settings.vulnerabilityCheckDelay}
                      onChange={(e) => handleSettingChange('vulnerabilityCheckDelay', parseInt(e.target.value))}
                      helperText="Delay between vulnerability checks to avoid API rate limits (0 for no delay)"
                      margin="normal"
                      disabled={!settings.vulnerabilityChecking}
                      InputProps={{
                        endAdornment: <InputAdornment position="end">seconds</InputAdornment>,
                      }}
                    />
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </TabPanel>

        {/* Updates & Cache Settings */}
        <TabPanel value={tabValue} index={4}>
          <Typography variant="h6" gutterBottom>
            Updates & Cache Settings
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <Box sx={{ mb: 3 }}>
                    <FormControlLabel
                      control={
                        <Switch
                          checked={settings.cacheEnabled}
                          onChange={(e) => handleSettingChange('cacheEnabled', e.target.checked)}
                        />
                      }
                      label="Enable Cache"
                    />

                    <Typography variant="body2" color="text.secondary" sx={{ mt: 1, mb: 2 }}>
                      Cache Maven Central responses to improve performance and reduce API calls
                    </Typography>

                    <TextField
                      fullWidth
                      label="Cache Duration"
                      variant="outlined"
                      type="number"
                      value={settings.cacheDuration}
                      onChange={(e) => handleSettingChange('cacheDuration', parseInt(e.target.value))}
                      helperText="How long to keep cached data (in hours)"
                      margin="normal"
                      disabled={!settings.cacheEnabled}
                      InputProps={{
                        endAdornment: <InputAdornment position="end">hours</InputAdornment>,
                      }}
                    />

                    <Box sx={{ mt: 2 }}>
                      <Button 
                        variant="outlined" 
                        startIcon={<RefreshIcon />}
                      >
                        Clear Cache
                      </Button>
                    </Box>
                  </Box>

                  <Divider sx={{ my: 2 }} />

                  <Box sx={{ mt: 3 }}>
                    <Typography variant="subtitle1" gutterBottom>
                      Default Chart Type
                    </Typography>

                    <FormControl fullWidth margin="normal">
                      <Select
                        value={settings.defaultChartType}
                        onChange={(e) => handleSettingChange('defaultChartType', e.target.value)}
                      >
                        <MenuItem value="pie">Pie Chart</MenuItem>
                        <MenuItem value="bar">Bar Chart</MenuItem>
                        <MenuItem value="line">Line Chart</MenuItem>
                      </Select>
                    </FormControl>
                  </Box>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </TabPanel>

        <Box sx={{ p: 3, display: 'flex', justifyContent: 'flex-end' }}>
          <Button 
            variant="outlined" 
            startIcon={<RefreshIcon />} 
            onClick={handleReset}
            sx={{ mr: 2 }}
          >
            Reset to Defaults
          </Button>
          <Button 
            variant="contained" 
            startIcon={<SaveIcon />} 
            onClick={handleSave}
          >
            Save Settings
          </Button>
        </Box>
      </Paper>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={5000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </>
  );
};

export default Settings;
