import React from 'react';
import { ResponsivePie } from '@nivo/pie';
import { Box, Typography, CircularProgress } from '@mui/material';

/**
 * Dependency Status Pie Chart component using Nivo
 * 
 * @param {Object} props - Component props
 * @param {Object} props.data - Analysis data containing dependency counts
 * @param {boolean} props.loading - Loading state
 * @param {number} props.height - Chart height in pixels
 */
const DependencyStatusChart = ({ data, loading, height = 300 }) => {
  // Return loading indicator if data is loading
  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height }}>
        <CircularProgress />
      </Box>
    );
  }

  // Return message if no data available
  if (!data || !data.upToDateDependencies) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height, p: 2 }}>
        <Typography color="text.secondary">No dependency data available</Typography>
      </Box>
    );
  }

  // Prepare data for the pie chart
  const chartData = [
    {
      id: 'Up-to-date',
      label: 'Up-to-date',
      value: data.upToDateDependencies || 0,
      color: '#4caf50' // green
    },
    {
      id: 'Outdated',
      label: 'Outdated',
      value: data.outdatedDependencies || 0,
      color: '#ff9800' // orange
    },
    {
      id: 'Unknown',
      label: 'Unknown',
      value: data.unidentifiedDependencies || 0,
      color: '#9e9e9e' // grey
    }
  ].filter(item => item.value > 0); // Only include items with values > 0

  // If no data points with values > 0, show a message
  if (chartData.length === 0) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height, p: 2 }}>
        <Typography color="text.secondary">No dependency data available</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ height }}>
      <ResponsivePie
        data={chartData}
        margin={{ top: 40, right: 80, bottom: 40, left: 80 }}
        innerRadius={0.5}
        padAngle={0.7}
        cornerRadius={3}
        activeOuterRadiusOffset={8}
        borderWidth={1}
        borderColor={{ from: 'color', modifiers: [['darker', 0.2]] }}
        arcLinkLabelsSkipAngle={10}
        arcLinkLabelsTextColor="#333333"
        arcLinkLabelsThickness={2}
        arcLinkLabelsColor={{ from: 'color' }}
        arcLabelsSkipAngle={10}
        arcLabelsTextColor={{ from: 'color', modifiers: [['darker', 2]] }}
        colors={{ datum: 'data.color' }}
        legends={[
          {
            anchor: 'right',
            direction: 'column',
            justify: false,
            translateX: 30,
            translateY: 0,
            itemsSpacing: 0,
            itemWidth: 100,
            itemHeight: 26,
            itemTextColor: '#999',
            itemDirection: 'left-to-right',
            itemOpacity: 1,
            symbolSize: 18,
            symbolShape: 'circle'
          }
        ]}
      />
    </Box>
  );
};

export default DependencyStatusChart;
