import React, { useState, useEffect } from 'react';
import {
  Typography,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Box,
  TextField,
  InputAdornment,
  IconButton,
  CircularProgress,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  Snackbar,
  Alert,
  Menu,
  ListItemIcon,
  ListItemText
} from '@mui/material';
import { Link as RouterLink, useNavigate } from 'react-router-dom';
import AddIcon from '@mui/icons-material/Add';
import SearchIcon from '@mui/icons-material/Search';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import FilterListIcon from '@mui/icons-material/FilterList';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import api from '../services/api';

const Projects = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [projects, setProjects] = useState([]);
  const [search, setSearch] = useState('');
  const [openNewProject, setOpenNewProject] = useState(false);
  const [newProject, setNewProject] = useState({
    name: '',
    description: '',
  });
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'info'
  });
  // Add states for menu
  const [menuAnchorEl, setMenuAnchorEl] = useState(null);
  const [selectedProject, setSelectedProject] = useState(null);
  // Add state for edit dialog
  const [openEditProject, setOpenEditProject] = useState(false);
  const [editProjectData, setEditProjectData] = useState({
    name: '',
    description: ''
  });
  // Add state for delete confirmation dialog
  const [openDeleteDialog, setOpenDeleteDialog] = useState(false);

  const closeSnackbar = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  useEffect(() => {
    // Fetch projects from the API
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    setLoading(true);
    try {
      const response = await api.projects.getAll();
      console.log('API Response:', response.data);
      setProjects(response.data || []);
    } catch (error) {
      console.error('Error fetching projects:', error);
      setSnackbar({
        open: true,
        message: 'Failed to load projects. Please try again.',
        severity: 'error'
      });
      // Set empty array if error occurs
      setProjects([]);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateProject = async () => {
    if (!newProject.name) {
      setSnackbar({
        open: true,
        message: 'Project name is required',
        severity: 'warning'
      });
      return;
    }

    try {
      console.log('Creating project with data:', newProject);
      const response = await api.projects.create(newProject);
      console.log('Create project response:', response.data);

      // Add the new project to the list
      setProjects([...projects, response.data]);

      // Reset form and close dialog
      setOpenNewProject(false);
      setNewProject({
        name: '',
        description: '',
      });

      setSnackbar({
        open: true,
        message: 'Project created successfully!',
        severity: 'success'
      });

      // Navigate to the new project
      navigate(`/projects/${response.data.id}`);
    } catch (error) {
      console.error('Error creating project:', error);
      setSnackbar({
        open: true,
        message: `Failed to create project: ${error.response?.data?.message || error.message}`,
        severity: 'error'
      });
    }
  };

  // Handler for opening menu
  const handleMenuOpen = (event, project) => {
    setMenuAnchorEl(event.currentTarget);
    setSelectedProject(project);
  };

  // Handler for closing menu
  const handleMenuClose = () => {
    setMenuAnchorEl(null);
  };

  // Handler for edit option
  const handleEditClick = () => {
    if (selectedProject) {
      setEditProjectData({
        name: selectedProject.name,
        description: selectedProject.description || ''
      });
      setOpenEditProject(true);
    }
    handleMenuClose();
  };

  // Handler for delete option
  const handleDeleteClick = () => {
    setOpenDeleteDialog(true);
    handleMenuClose();
  };

  // Handler for updating a project
  const handleUpdateProject = async () => {
    if (!editProjectData.name) {
      setSnackbar({
        open: true,
        message: 'Project name is required',
        severity: 'warning'
      });
      return;
    }

    try {
      const response = await api.projects.update(selectedProject.id, editProjectData);

      // Update the project in the list
      setProjects(projects.map(project => 
        project.id === selectedProject.id ? response.data : project
      ));

      // Close dialog and reset form
      setOpenEditProject(false);
      setEditProjectData({ name: '', description: '' });

      setSnackbar({
        open: true,
        message: 'Project updated successfully!',
        severity: 'success'
      });
    } catch (error) {
      console.error('Error updating project:', error);
      setSnackbar({
        open: true,
        message: `Failed to update project: ${error.response?.data?.message || error.message}`,
        severity: 'error'
      });
    }
  };

  // Handler for deleting a project
  const handleDeleteProject = async () => {
    try {
      await api.projects.delete(selectedProject.id);

      // Remove the project from the list
      setProjects(projects.filter(project => project.id !== selectedProject.id));

      // Close dialog
      setOpenDeleteDialog(false);

      setSnackbar({
        open: true,
        message: 'Project deleted successfully!',
        severity: 'success'
      });
    } catch (error) {
      console.error('Error deleting project:', error);
      setSnackbar({
        open: true,
        message: `Failed to delete project: ${error.response?.data?.message || error.message}`,
        severity: 'error'
      });
      setOpenDeleteDialog(false);
    }
  };

  const filteredProjects = projects.filter(project =>
    project.name?.toLowerCase().includes(search.toLowerCase()) ||
    project.description?.toLowerCase().includes(search.toLowerCase())
  );

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 10 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4" component="h1">
          Projects
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={() => setOpenNewProject(true)}
        >
          New Project
        </Button>
      </Box>

      <Paper sx={{ mb: 4, p: 2 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 2 }}>
          <TextField
            placeholder="Search projects..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            variant="outlined"
            size="small"
            sx={{ width: '40%' }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon />
                </InputAdornment>
              ),
            }}
          />
          <Button startIcon={<FilterListIcon />}>
            Filters
          </Button>
        </Box>

        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Project Name</TableCell>
                <TableCell>Description</TableCell>
                <TableCell>Last Analysis</TableCell>
                <TableCell>Dependencies</TableCell>
                <TableCell>Status</TableCell>
                <TableCell align="right">Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {filteredProjects.length > 0 ? (
                filteredProjects.map((project) => (
                  <TableRow key={project.id} hover>
                    <TableCell>
                      <Typography
                        variant="subtitle2"
                        component={RouterLink}
                        to={`/projects/${project.id}`}
                        sx={{ textDecoration: 'none', color: 'primary.main', fontWeight: 'medium' }}
                      >
                        {project.name}
                      </Typography>
                    </TableCell>
                    <TableCell>{project.description || 'No description'}</TableCell>
                    <TableCell>{project.lastAnalysisDate || 'Never'}</TableCell>
                    <TableCell>
                      {project.dependencyCount > 0 ? (
                        <Box>
                          <Typography variant="body2">
                            {project.dependencyCount} total
                          </Typography>
                          <Typography variant="body2" color="warning.main">
                            {project.outdatedCount || 0} outdated
                          </Typography>
                        </Box>
                      ) : (
                        'No analysis'
                      )}
                    </TableCell>
                    <TableCell>
                      <Chip
                        label={project.status === 'INACTIVE' ? 'Inactive' : 'Active'}
                        color={project.status !== 'INACTIVE' ? 'success' : 'default'}
                        size="small"
                      />
                    </TableCell>
                    <TableCell align="right">
                      <Button
                        variant="outlined"
                        size="small"
                        component={RouterLink}
                        to={`/projects/${project.id}/new-analysis`}
                        sx={{ mr: 1 }}
                      >
                        Analyze
                      </Button>
                      <IconButton size="small" onClick={(e) => handleMenuOpen(e, project)}>
                        <MoreVertIcon />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={6} align="center">
                    No projects found
                  </TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>

      {/* Create Project Dialog */}
      <Dialog open={openNewProject} onClose={() => setOpenNewProject(false)}>
        <DialogTitle>Create New Project</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Project Name"
            type="text"
            fullWidth
            required
            value={newProject.name}
            onChange={(e) => setNewProject({ ...newProject, name: e.target.value })}
          />
          <TextField
            margin="dense"
            id="description"
            label="Description"
            type="text"
            fullWidth
            multiline
            rows={4}
            value={newProject.description}
            onChange={(e) => setNewProject({ ...newProject, description: e.target.value })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenNewProject(false)}>Cancel</Button>
          <Button onClick={handleCreateProject} variant="contained">Create</Button>
        </DialogActions>
      </Dialog>

      {/* Edit Project Dialog */}
      <Dialog open={openEditProject} onClose={() => setOpenEditProject(false)}>
        <DialogTitle>Edit Project</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            id="edit-name"
            label="Project Name"
            type="text"
            fullWidth
            required
            value={editProjectData.name}
            onChange={(e) => setEditProjectData({ ...editProjectData, name: e.target.value })}
          />
          <TextField
            margin="dense"
            id="edit-description"
            label="Description"
            type="text"
            fullWidth
            multiline
            rows={4}
            value={editProjectData.description}
            onChange={(e) => setEditProjectData({ ...editProjectData, description: e.target.value })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenEditProject(false)}>Cancel</Button>
          <Button onClick={handleUpdateProject} variant="contained">Save</Button>
        </DialogActions>
      </Dialog>

      {/* Delete Confirmation Dialog */}
      <Dialog open={openDeleteDialog} onClose={() => setOpenDeleteDialog(false)}>
        <DialogTitle>Delete Project</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to delete "{selectedProject?.name}"? This action cannot be undone.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenDeleteDialog(false)}>Cancel</Button>
          <Button onClick={handleDeleteProject} variant="contained" color="error">Delete</Button>
        </DialogActions>
      </Dialog>

      {/* Action Menu */}
      <Menu
        anchorEl={menuAnchorEl}
        open={Boolean(menuAnchorEl)}
        onClose={handleMenuClose}
      >
        <MenuItem onClick={handleEditClick}>
          <ListItemIcon>
            <EditIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText primary="Edit" />
        </MenuItem>
        <MenuItem onClick={handleDeleteClick}>
          <ListItemIcon>
            <DeleteIcon fontSize="small" />
          </ListItemIcon>
          <ListItemText primary="Delete" />
        </MenuItem>
      </Menu>

      {/* Snackbar for notifications */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={closeSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert onClose={closeSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </>
  );
};

export default Projects;
