import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Typography,
  Paper,
  Box,
  Grid,
  Tabs,
  Tab,
  Card,
  CardContent,
  Divider,
  CircularProgress,
  Button,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Tooltip,
  TextField,
  InputAdornment,
  Container,
  Stack,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import DownloadIcon from '@mui/icons-material/Download';
import ShareIcon from '@mui/icons-material/Share';
import SearchIcon from '@mui/icons-material/Search';
import WarningIcon from '@mui/icons-material/Warning';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import UpdateIcon from '@mui/icons-material/Update';
import InfoIcon from '@mui/icons-material/Info';
import LockIcon from '@mui/icons-material/Lock';
import RefreshIcon from '@mui/icons-material/Refresh';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import api from '../services/api';

// Sample chart component (to be replaced with a real chart library)
const SampleChart = ({ title, type }) => {
  return (
    <Box
      sx={{
        height: 250,
        bgcolor: 'rgba(33, 150, 243, 0.1)',
        borderRadius: 1,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column',
        p: 2,
      }}
    >
      <Typography variant="subtitle1" gutterBottom>
        {title}
      </Typography>
      <Typography color="text.secondary">
        {type} Chart
      </Typography>
    </Box>
  );
};

// Tab panel component for tab content
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`analysis-tabpanel-${index}`}
      aria-labelledby={`analysis-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

const AnalysisDetail = () => {
  const { id: analysisId } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [analysis, setAnalysis] = useState(null);
  const [tabValue, setTabValue] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');
  const [dependencies, setDependencies] = useState([]);
  const [error, setError] = useState(null);
  const [filter, setFilter] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'ascending' });
  const [checkingVulnerabilities, setCheckingVulnerabilities] = useState(false);
  const [pollingStatus, setPollingStatus] = useState(false);

  // Function to fetch analysis data
  const fetchAnalysis = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.dependencyAnalysis.getById(analysisId);
      setAnalysis(response.data);

      // Start polling if vulnerability check is in progress
      if (response.data.vulnerabilityCheckStatus === 'IN_PROGRESS') {
        setPollingStatus(true);
      }

      setError(null);
    } catch (err) {
      console.error('Error fetching analysis:', err);
      setError('Failed to load analysis. Please try again later.');
    } finally {
      setLoading(false);
    }
  }, [analysisId]);

  // Fetch analysis data on component mount
  useEffect(() => {
    fetchAnalysis();
  }, [fetchAnalysis]);

  // Polling logic for vulnerability check status
  useEffect(() => {
    let intervalId;

    if (pollingStatus) {
      intervalId = setInterval(async () => {
        try {
          const statusResponse = await api.dependencyAnalysis.getVulnerabilityStatus(analysisId);

          if (statusResponse.data.status === 'COMPLETED' || statusResponse.data.status === 'FAILED') {
            setPollingStatus(false);
            fetchAnalysis(); // Refresh analysis data when check is complete
          }
        } catch (err) {
          console.error('Error polling vulnerability status:', err);
          setPollingStatus(false);
        }
      }, 3000); // Poll every 3 seconds
    }

    return () => {
      if (intervalId) {
        clearInterval(intervalId);
      }
    };
  }, [pollingStatus, analysisId, fetchAnalysis]);

  // Handler for starting vulnerability check
  const handleStartVulnerabilityCheck = async () => {
    try {
      setCheckingVulnerabilities(true);
      await api.dependencyAnalysis.checkVulnerabilities(analysisId);
      setPollingStatus(true);
    } catch (err) {
      console.error('Error starting vulnerability check:', err);
      setError('Failed to start vulnerability check. Please try again later.');
    } finally {
      setCheckingVulnerabilities(false);
    }
  };

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  const filteredDependencies = dependencies.filter(dep => 
    dep.groupId.toLowerCase().includes(searchQuery.toLowerCase()) ||
    dep.artifactId.toLowerCase().includes(searchQuery.toLowerCase()) ||
    dep.currentVersion.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Get vulnerability status text
  const getVulnerabilityStatusText = () => {
    switch (analysis.vulnerabilityCheckStatus) {
      case 'PENDING':
        return 'Vulnerability check has not been started';
      case 'IN_PROGRESS':
        return 'Checking for vulnerabilities...';
      case 'COMPLETED':
        return analysis.vulnerabilities > 0 
          ? `Found ${analysis.vulnerabilities} vulnerabilities` 
          : 'No vulnerabilities found';
      case 'FAILED':
        return 'Vulnerability check failed';
      default:
        return 'Unknown status';
    }
  };

  const getVulnerabilityStatusIcon = () => {
    switch (analysis.vulnerabilityCheckStatus) {
      case 'PENDING':
        return <PlayArrowIcon />;
      case 'IN_PROGRESS':
        return <CircularProgress size={20} />;
      case 'COMPLETED':
        return analysis.vulnerabilities > 0 
          ? <WarningIcon color="error" /> 
          : <CheckCircleIcon color="success" />;
      case 'FAILED':
        return <WarningIcon color="warning" />;
      default:
        return null;
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 10 }}>
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ mt: 4, p: 2 }}>
        <Typography color="error">{error}</Typography>
        <Button variant="outlined" onClick={fetchAnalysis} sx={{ mt: 2 }}>
          Try Again
        </Button>
      </Box>
    );
  }

  if (!analysis) {
    return (
      <Box sx={{ mt: 4, p: 2 }}>
        <Typography>Analysis not found</Typography>
        <Button variant="outlined" onClick={() => navigate('/dashboard')} sx={{ mt: 2 }}>
          Back to Dashboard
        </Button>
      </Box>
    );
  }

  return (
    <Container maxWidth="xl">
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <IconButton 
          onClick={() => navigate(-1)} 
          sx={{ mr: 1 }}
        >
          <ArrowBackIcon />
        </IconButton>
        <Typography variant="h4" component="h1" sx={{ flexGrow: 1 }}>
          Analysis Details
        </Typography>
        <Button 
          variant="outlined" 
          startIcon={<DownloadIcon />}
          sx={{ mr: 1 }}
        >
          Export
        </Button>
        <Button
          variant="outlined"
          startIcon={<ShareIcon />}
        >
          Share
        </Button>
      </Box>

      <Paper sx={{ p: 3, mb: 4 }}>
        <Grid container spacing={2}>
          <Grid item xs={12} md={8}>
            <Typography variant="h6">
              Project: {analysis.projectName}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Analysis completed on {analysis.date}
            </Typography>
          </Grid>
          <Grid item xs={12} md={4} sx={{ display: 'flex', justifyContent: { xs: 'flex-start', md: 'flex-end' } }}>
            <Box>
              <Chip 
                label={analysis.status === 'completed' ? 'Completed' : 'In Progress'} 
                color={analysis.status === 'completed' ? 'success' : 'primary'} 
                sx={{ mb: 1 }}
              />
              <Typography variant="body2" color="text.secondary">
                ID: {analysis.id}
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </Paper>

      {/* Summary Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={6} sm={3}>
          <Paper
            elevation={0}
            sx={{
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              bgcolor: 'rgba(33, 150, 243, 0.1)',
              borderRadius: 2,
            }}
          >
            <Typography color="text.secondary" variant="body2">
              Total Dependencies
            </Typography>
            <Typography variant="h5" sx={{ mt: 1 }}>
              {analysis.totalDependencies}
            </Typography>
          </Paper>
        </Grid>

        <Grid item xs={6} sm={3}>
          <Paper
            elevation={0}
            sx={{
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              bgcolor: 'rgba(255, 152, 0, 0.1)',
              borderRadius: 2,
            }}
          >
            <Typography color="text.secondary" variant="body2">
              Outdated
            </Typography>
            <Typography variant="h5" sx={{ mt: 1, color: 'warning.main' }}>
              {analysis.outdatedDependencies}
            </Typography>
          </Paper>
        </Grid>

        <Grid item xs={6} sm={3}>
          <Paper
            elevation={0}
            sx={{
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              bgcolor: 'rgba(244, 67, 54, 0.1)',
              borderRadius: 2,
            }}
          >
            <Typography color="text.secondary" variant="body2">
              Vulnerabilities
            </Typography>
            <Typography variant="h5" sx={{ mt: 1, color: 'error.main' }}>
              {analysis.vulnerabilities}
            </Typography>
          </Paper>
        </Grid>

        <Grid item xs={6} sm={3}>
          <Paper
            elevation={0}
            sx={{
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              bgcolor: 'rgba(156, 39, 176, 0.1)',
              borderRadius: 2,
            }}
          >
            <Typography color="text.secondary" variant="body2">
              License Issues
            </Typography>
            <Typography variant="h5" sx={{ mt: 1, color: '#9c27b0' }}>
              {analysis.licenseIssues}
            </Typography>
          </Paper>
        </Grid>
      </Grid>

      <Paper sx={{ mb: 4 }}>
        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <Tabs 
            value={tabValue} 
            onChange={handleTabChange} 
            aria-label="analysis tabs"
            variant="scrollable"
            scrollButtons="auto"
          >
            <Tab label="Overview" />
            <Tab label="Dependencies" />
            <Tab label="Charts" />
            <Tab label="Vulnerabilities" />
            <Tab label="Licenses" />
          </Tabs>
        </Box>

        <TabPanel value={tabValue} index={0}>
          <Typography variant="h6" gutterBottom>
            Analysis Overview
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <SampleChart title="Dependency Status" type="Pie" />
            </Grid>
            <Grid item xs={12} md={6}>
              <SampleChart title="Security Overview" type="Bar" />
            </Grid>
          </Grid>

          <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>
            Summary
          </Typography>
          <Typography variant="body1" paragraph>
            This analysis found {analysis.outdatedDependencies} outdated dependencies, {analysis.vulnerabilities} security vulnerabilities, and {analysis.licenseIssues} license compliance issues.
          </Typography>
          <Typography variant="body1" paragraph>
            It's recommended to update the dependencies to their latest versions to resolve security issues and take advantage of the latest features and bug fixes.
          </Typography>

          <Box sx={{ mt: 2 }}>
            <Button variant="contained">Generate Full Report</Button>
          </Box>
        </TabPanel>

        <TabPanel value={tabValue} index={1}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
            <Typography variant="h6">
              Dependencies ({filteredDependencies.length})
            </Typography>
            <TextField
              placeholder="Search dependencies..."
              size="small"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
            />
          </Box>

          <TableContainer>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Group ID</TableCell>
                  <TableCell>Artifact ID</TableCell>
                  <TableCell>Current Version</TableCell>
                  <TableCell>Latest Version</TableCell>
                  <TableCell>Scope</TableCell>
                  <TableCell>Status</TableCell>
                  <TableCell>License</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredDependencies.map((dep) => (
                  <TableRow key={dep.id} hover>
                    <TableCell>{dep.groupId}</TableCell>
                    <TableCell>{dep.artifactId}</TableCell>
                    <TableCell>{dep.currentVersion}</TableCell>
                    <TableCell>
                      {dep.latestVersion}
                      {dep.latestVersion !== dep.currentVersion && 
                       !dep.currentVersion.includes('MANAGED_BY_BOM') && (
                        <Chip 
                          label="Update" 
                          size="small" 
                          color="primary" 
                          variant="outlined" 
                          sx={{ ml: 1 }} 
                        />
                      )}
                    </TableCell>
                    <TableCell>
                      <Chip
                        label={dep.scope}
                        size="small"
                        variant="outlined"
                      />
                    </TableCell>
                    <TableCell>
                      {dep.status === 'uptodate' ? (
                        <Tooltip title="Up to date">
                          <CheckCircleIcon color="success" fontSize="small" />
                        </Tooltip>
                      ) : (dep.status === 'outdated' && !dep.currentVersion.includes('MANAGED_BY_BOM')) ? (
                        <Tooltip title="Outdated">
                          <UpdateIcon color="warning" fontSize="small" />
                        </Tooltip>
                      ) : null}
                      {dep.vulnerabilities > 0 && (
                        <Tooltip title={`${dep.vulnerabilities} vulnerabilities found`}>
                          <WarningIcon color="error" fontSize="small" sx={{ ml: 1 }} />
                        </Tooltip>
                      )}
                    </TableCell>
                    <TableCell>
                      <Tooltip title={dep.license}>
                        <Chip
                          icon={<LockIcon fontSize="small" />}
                          label={dep.license}
                          size="small"
                          variant="outlined"
                        />
                      </Tooltip>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>

        <TabPanel value={tabValue} index={2}>
          <Typography variant="h6" gutterBottom>
            Visualization Charts
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <SampleChart title="Dependency Update Status" type="Pie" />
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <SampleChart title="Vulnerabilities by Severity" type="Bar" />
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <SampleChart title="License Distribution" type="Donut" />
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <SampleChart title="Dependencies by Scope" type="Bar" />
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </TabPanel>

        <TabPanel value={tabValue} index={3}>
          <Box sx={{ mb: 3, display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
            <Typography variant="h6" gutterBottom>
              Security Vulnerabilities
            </Typography>

            <Box sx={{ display: 'flex', alignItems: 'center' }}>
              <Typography variant="body2" color="text.secondary" sx={{ mr: 2 }}>
                Status: {getVulnerabilityStatusText()}
              </Typography>

              {analysis.vulnerabilityCheckStatus === 'PENDING' && (
                <Button 
                  variant="contained" 
                  color="primary" 
                  startIcon={<PlayArrowIcon />}
                  onClick={handleStartVulnerabilityCheck}
                  disabled={checkingVulnerabilities}
                >
                  {checkingVulnerabilities ? 'Starting...' : 'Check Vulnerabilities'}
                </Button>
              )}

              {(analysis.vulnerabilityCheckStatus === 'COMPLETED' || analysis.vulnerabilityCheckStatus === 'FAILED') && (
                <Button 
                  variant="outlined" 
                  color="primary" 
                  startIcon={<RefreshIcon />}
                  onClick={handleStartVulnerabilityCheck}
                  disabled={checkingVulnerabilities}
                >
                  {checkingVulnerabilities ? 'Starting...' : 'Re-check Vulnerabilities'}
                </Button>
              )}
            </Box>
          </Box>

          {analysis.vulnerabilityCheckStatus === 'IN_PROGRESS' && (
            <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', my: 4 }}>
              <CircularProgress size={60} />
              <Typography variant="h6" sx={{ mt: 2 }}>
                Checking for vulnerabilities...
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                This may take a few minutes for large projects.
              </Typography>
            </Box>
          )}

          {analysis.vulnerabilityCheckStatus === 'COMPLETED' && (
            analysis.vulnerabilities > 0 ? (
              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Dependency</TableCell>
                      <TableCell>Vulnerability</TableCell>
                      <TableCell>Severity</TableCell>
                      <TableCell>Affected Versions</TableCell>
                      <TableCell>Fixed In</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {analysis.dependencies
                      .filter(dep => dep.isVulnerable)
                      .flatMap(dep => 
                        dep.vulnerabilities.map((vuln, index) => (
                          <TableRow key={`${dep.id}-${index}`} hover>
                            <TableCell>{`${dep.groupId}:${dep.artifactId}:${dep.currentVersion}`}</TableCell>
                            <TableCell>{vuln.name}</TableCell>
                            <TableCell>
                              <Chip 
                                label={vuln.severity} 
                                color={
                                  vuln.severity === 'Critical' ? 'error' :
                                  vuln.severity === 'High' ? 'error' :
                                  vuln.severity === 'Medium' ? 'warning' : 'info'
                                } 
                                size="small" 
                              />
                            </TableCell>
                            <TableCell>{vuln.affectedVersions}</TableCell>
                            <TableCell>{vuln.fixedInVersion}</TableCell>
                          </TableRow>
                        ))
                      )
                    }
                  </TableBody>
                </Table>
              </TableContainer>
            ) : (
              <Box sx={{ p: 4, textAlign: 'center' }}>
                <CheckCircleIcon color="success" sx={{ fontSize: 48, mb: 2 }} />
                <Typography variant="h6" gutterBottom>
                  No vulnerabilities found
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  All your dependencies are currently free from known security vulnerabilities.
                </Typography>
              </Box>
            )
          )}

          {analysis.vulnerabilityCheckStatus === 'FAILED' && (
            <Box sx={{ p: 4, textAlign: 'center' }}>
              <WarningIcon color="warning" sx={{ fontSize: 48, mb: 2 }} />
              <Typography variant="h6" gutterBottom>
                Vulnerability check failed
              </Typography>
              <Typography variant="body2" color="text.secondary">
                There was an error checking for vulnerabilities. Please try again later.
              </Typography>
            </Box>
          )}

          {analysis.vulnerabilityCheckStatus === 'PENDING' && (
            <Box sx={{ p: 4, textAlign: 'center' }}>
              <Typography variant="body1" sx={{ mb: 2 }}>
                Click the "Check Vulnerabilities" button to scan your dependencies for security issues.
              </Typography>
              <Typography variant="body2" color="text.secondary">
                This feature uses the Sonatype OSS Index to identify known vulnerabilities in your dependencies.
              </Typography>
            </Box>
          )}
        </TabPanel>

        <TabPanel value={tabValue} index={4}>
          <Typography variant="h6" gutterBottom>
            License Compliance
          </Typography>

          {analysis.licenseIssues > 0 ? (
            <>
              <Typography variant="body2" color="text.secondary" paragraph>
                The following dependencies have license issues that may need attention:
              </Typography>

              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Dependency</TableCell>
                      <TableCell>License</TableCell>
                      <TableCell>Issue</TableCell>
                      <TableCell>Recommendation</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    <TableRow hover>
                      <TableCell>mysql:mysql-connector-java:8.0.28</TableCell>
                      <TableCell>
                        <Chip label="GPL" size="small" />
                      </TableCell>
                      <TableCell>GPL is copyleft license and may be incompatible with commercial use</TableCell>
                      <TableCell>Consider using a commercial license or alternative database connector</TableCell>
                    </TableRow>
                    <TableRow hover>
                      <TableCell>unknown:library:1.0.0</TableCell>
                      <TableCell>
                        <Chip label="Unknown" size="small" />
                      </TableCell>
                      <TableCell>No license information available</TableCell>
                      <TableCell>Verify license information with the library author</TableCell>
                    </TableRow>
                  </TableBody>
                </Table>
              </TableContainer>
            </>
          ) : (
            <Box sx={{ p: 4, textAlign: 'center' }}>
              <CheckCircleIcon color="success" sx={{ fontSize: 48, mb: 2 }} />
              <Typography variant="h6" gutterBottom>
                No license issues found
              </Typography>
              <Typography variant="body2" color="text.secondary">
                All your dependencies have compatible licenses.
              </Typography>
            </Box>
          )}
        </TabPanel>
      </Paper>
    </Container>
  );
};

export default AnalysisDetail;
