                isNull: dep.license === null,
                isUndefined: dep.license === undefined,
                isEmpty: dep.license === '',
                isUnknown: dep.license === 'Unknown'
              });

              // Consider unknown or missing licenses as restricted
              if (!dep.license || dep.license === 'Unknown' || dep.license.trim() === '') {
                return true;
              }

              // Check if license contains any restricted term (case-insensitive)
              return restrictedLicenses.some(
                restricted => dep.license.toUpperCase().includes(restricted.toUpperCase())
              );
            }) : []
          })}

          {(analysis.licenseIssues || 0) > 0 ? (
            <>
              <Typography variant="body2" color="text.secondary" paragraph>
                The following dependencies have license issues that may need attention:
              </Typography>

              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Dependency</TableCell>
                      <TableCell>License</TableCell>
                      <TableCell>Issue</TableCell>
                      <TableCell>Recommendation</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {analysis.dependencies && analysis.dependencies.length > 0 ? (
                      analysis.dependencies
                        .filter(dep => {
                          // Check if the license is in the restricted list or unknown/empty
                          const restrictedLicenses = ['GPL', 'AGPL', 'LGPL', 'GPL-2.0', 'GPL-3.0', 'AGPL-3.0', 'LGPL-2.1', 'LGPL-3.0', 'UNKNOWN'];

                          // Consider unknown or missing licenses as restricted
                          if (!dep.license || dep.license === 'Unknown' || dep.license.trim() === '') {
                            console.log(`Dependency ${dep.groupId}:${dep.artifactId} has missing/unknown license - should be shown`);
                            return true;
                          }

                          // Check if license contains any restricted term (case-insensitive)
                          const isRestricted = restrictedLicenses.some(
                            restricted => dep.license.toUpperCase().includes(restricted.toUpperCase())
                          );
                          if (isRestricted) {
                            console.log(`Dependency ${dep.groupId}:${dep.artifactId} has restricted license: ${dep.license}`);
                          }
                          return isRestricted;
                        })
                        // Log filtered dependencies before mapping
                        .map(dep => {
                          console.log('Mapping license issue dependency:', dep);
                          return (
                            <TableRow key={dep.id} hover>
                              <TableCell>
                                {dep.groupId}:{dep.artifactId}:{dep.version}
                              </TableCell>
                              <TableCell>
                                <Chip 
                                  label={dep.license || 'Unknown'} 
                                  size="small"
                                  color={!dep.license ? 'warning' : dep.license.toUpperCase().includes('GPL') ? 'error' : 'default'}
                                />
                              </TableCell>
                              <TableCell>
                                {!dep.license ? 'No license information available' : 
                                dep.license.toUpperCase().includes('GPL') ? 'GPL is a copyleft license and may be incompatible with commercial use' :
                                'License may have restrictions that affect your project'}
                              </TableCell>
                              <TableCell>
                                {!dep.license ? 'Verify license information with the library author' : 
                                dep.license.toUpperCase().includes('GPL') ? 'Consider using a commercial license or alternative library' :
                                'Review license terms and consult legal counsel if necessary'}
                              </TableCell>
                            </TableRow>
                          );
                        })
                    ) : (
                      <TableRow>
                        <TableCell colSpan={4} align="center">
                          No dependencies data available
                        </TableCell>
                      </TableRow>
                    )}
                  </TableBody>
                </Table>
              </TableContainer>
            </>
          ) : (
            <Box sx={{ p: 4, textAlign: 'center' }}>
              <CheckCircleIcon color="success" sx={{ fontSize: 48, mb: 2 }} />
              <Typography variant="h6" gutterBottom>
                No license issues found
              </Typography>
              <Typography variant="body2" color="text.secondary">
                All your dependencies have compatible licenses.
              </Typography>
            </Box>
          )}
        </TabPanel>
      </Paper>
      <Snackbar
        open={snackbar.open}
        autoHideDuration={5000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </>
  );
};

export default AnalysisDetail;
