import React, { useMemo } from 'react';
import { ResponsiveBar } from '@nivo/bar';
import { Box, Typography, CircularProgress } from '@mui/material';

/**
 * Vulnerability Bar Chart component using Nivo
 * 
 * @param {Object} props - Component props
 * @param {Object} props.data - Analysis data containing dependencies with vulnerabilities
 * @param {boolean} props.loading - Loading state
 * @param {number} props.height - Chart height in pixels
 */
const VulnerabilityChart = ({ data, loading, height = 300 }) => {
  // Return loading indicator if data is loading
  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height }}>
        <CircularProgress />
      </Box>
    );
  }

  // Return message if no data available
  if (!data || !data.dependencies) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height, p: 2 }}>
        <Typography color="text.secondary">No vulnerability data available</Typography>
      </Box>
    );
  }

  // Process data to get vulnerability counts by severity
  const vulnerabilityData = useMemo(() => {
    // Initialize counters for each severity level
    const counts = {
      Critical: 0,
      High: 0,
      Medium: 0,
      Low: 0,
      None: 0
    };

    // Count vulnerabilities by severity
    data.dependencies?.forEach(dependency => {
      if (dependency.vulnerabilities) {
        dependency.vulnerabilities.forEach(vuln => {
          // Extract severity from the vulnerability - assuming this exists in your data
          // If not, you might need to adapt this based on your actual data structure
          const severity = vuln.severity || 'Unknown';
          if (counts[severity] !== undefined) {
            counts[severity]++;
          }
        });
      }
    });

    // Convert to the format expected by the bar chart
    return Object.keys(counts)
      .filter(key => counts[key] > 0 && key !== 'None') // Only include non-zero values and exclude 'None'
      .map(key => ({
        severity: key,
        count: counts[key],
        color: getColorForSeverity(key)
      }));
  }, [data]);

  // If no vulnerability data, show a message
  if (vulnerabilityData.length === 0) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height, p: 2 }}>
        <Typography color="text.secondary">No vulnerability data found</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ height }}>
      <ResponsiveBar
        data={vulnerabilityData}
        keys={['count']}
        indexBy="severity"
        margin={{ top: 40, right: 50, bottom: 40, left: 60 }}
        padding={0.3}
        valueScale={{ type: 'linear' }}
        indexScale={{ type: 'band', round: true }}
        colors={({ data }) => data.color}
        borderColor={{ from: 'color', modifiers: [['darker', 1.6]] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: 0,
          legend: 'Severity',
          legendPosition: 'middle',
          legendOffset: 32
        }}
        axisLeft={{
          tickSize: 5,
          tickPadding: 5,
          tickRotation: 0,
          legend: 'Count',
          legendPosition: 'middle',
          legendOffset: -40
        }}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor={{ from: 'color', modifiers: [['darker', 1.6]] }}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
      />
    </Box>
  );
};

// Helper function to get color based on severity
function getColorForSeverity(severity) {
  switch (severity) {
    case 'Critical':
      return '#d32f2f'; // dark red
    case 'High':
      return '#f44336'; // red
    case 'Medium':
      return '#ff9800'; // orange
    case 'Low':
      return '#ffeb3b'; // yellow
    default:
      return '#9e9e9e'; // grey
  }
}

export default VulnerabilityChart;
