import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Typography,
  Paper,
  Box,
  Button,
  TextField,
  CircularProgress,
  Stepper,
  Step,
  StepLabel,
  Grid,
  Card,
  CardContent,
  Divider,
  Alert,
  Chip,
  IconButton,
  FormControlLabel,
  Switch,
  Snackbar,
} from '@mui/material';
import CloudUploadIcon from '@mui/icons-material/CloudUpload';
import CodeIcon from '@mui/icons-material/Code';
import DescriptionIcon from '@mui/icons-material/Description';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import CloseIcon from '@mui/icons-material/Close';
import api from '../services/api';
import { toast } from 'react-hot-toast';

const steps = ['Select Input Method', 'Configure Analysis', 'Review & Start'];

const NewAnalysis = () => {
  const { id: projectId } = useParams();
  const navigate = useNavigate();
  const fileInputRef = useRef(null);

  const [activeStep, setActiveStep] = useState(0);
  const [loading, setLoading] = useState(true);
  const [project, setProject] = useState(null);
  const [inputMethod, setInputMethod] = useState('file'); // 'file' or 'manual'
  const [pomFile, setPomFile] = useState(null);
  const [pomContent, setPomContent] = useState('');
  const [pomDirectoryPath, setPomDirectoryPath] = useState('');
  const [analysisOptions, setAnalysisOptions] = useState({
    checkVulnerabilities: true,
    checkLicenses: true,
    generateReport: true,
    includeTransitive: true,
    notifyOnCompletion: false,
    resolveBomVersions: false,
  });
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState(null);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'info'
  });

  useEffect(() => {
    // Actually fetch the project data from the API
    const fetchProject = async () => {
      try {
        const response = await api.projects.getById(projectId);
        setProject(response.data);
      } catch (error) {
        console.error('Error fetching project:', error);
        setError('Failed to load project details. Please try again.');
        setProject({
          id: parseInt(projectId),
          name: 'Unknown Project',
          description: 'Project details could not be loaded',
        });
      } finally {
        setLoading(false);
      }
    };

    fetchProject();
  }, [projectId]);

  useEffect(() => {
    // Reset directory path and BOM resolution when input method changes
    if (inputMethod === 'manual') {
      // Reset directory path
      setPomDirectoryPath('');
      // Disable BOM resolution for manual input
      setAnalysisOptions(prev => ({
        ...prev,
        resolveBomVersions: false
      }));
    }
  }, [inputMethod]);

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setPomFile(file);

      // Try to get the full path from the file object
      let pomPath = '';

      // Check if we can access the path (this varies by browser security)
      // For Chrome/Edge, we have webkitRelativePath property for directory upload
      if (file.path) {
        pomPath = file.path;
      } else if (file.webkitRelativePath) {
        // This is available when using <input type="file" webkitdirectory> 
        pomPath = file.webkitRelativePath;
      } else {
        // Modern browsers don't expose full path for security reasons
        // We just know the file name
        pomPath = '';
      }

      // If we got a path, set it as the pomDirectoryPath
      if (pomPath) {
        // Extract the directory path (remove the filename)
        const lastSlashIndex = pomPath.lastIndexOf('/');
        if (lastSlashIndex !== -1) {
          const directoryPath = pomPath.substring(0, lastSlashIndex);
          setPomDirectoryPath(directoryPath);

          // Automatically enable BOM resolution if we have the path
          setAnalysisOptions(prev => ({
            ...prev,
            resolveBomVersions: true
          }));
        }
      } else {
        // If no path was detected, check if the file name is specifically 'pom.xml'
        // If so, suggest that we can try to resolve BOM-managed versions
        if (file.name.toLowerCase() === 'pom.xml') {
          // Set a flag to show a prompt asking the user if they want to enable BOM resolution
          // Instead of setting a flag, we'll just enable the option but not auto-fill the path
          setAnalysisOptions(prev => ({
            ...prev,
            resolveBomVersions: true
          }));
          // Show a message about providing the path
          setSnackbar({
            open: true,
            message: 'You uploaded a pom.xml file. If you want to resolve BOM-managed versions, please provide the path to the directory containing this file.',
            severity: 'info'
          });
        }
      }

      // Read file content
      const reader = new FileReader();
      reader.onload = (e) => {
        setPomContent(e.target.result);
      };
      reader.readAsText(file);
    }
  };

  const handleFileClick = () => {
    fileInputRef.current.click();
  };

  const handleOptionChange = (option) => {
    setAnalysisOptions((prev) => ({
      ...prev,
      [option]: !prev[option],
    }));
  };

  const submitAnalysis = async () => {
    setActiveStep(2);
    setLoading(true);
    setError(null);

    try {
      const analysisData = {
        projectId: projectId,
        pomContent: pomContent,
        checkVulnerabilities: analysisOptions.checkVulnerabilities
      };

      // Add pomDirectoryPath if BOM resolution is enabled
      if (analysisOptions.resolveBomVersions && pomDirectoryPath.trim()) {
        analysisData.pomDirectoryPath = pomDirectoryPath;
      }

      console.log('Submitting analysis request:', analysisData);

      // Use the new method that handles both analysis creation and redirection
      await api.dependencyAnalysis.startAnalysisAndRedirect(
        projectId, 
        analysisData, 
        navigate, 
        analysisOptions.notifyOnCompletion ? toast : null
      );

      // No need to handle redirect here anymore as it's done in the API service
      // The rest of the function's error handling remains as a fallback
    } catch (err) {
      console.error('Error submitting analysis:', err);

      // Determine error message based on response
      let errorMessage = 'An unexpected error occurred. Please try again later.';

      if (err.response) {
        // Server responded with a non-2xx status
        if (err.response.status === 400) {
          errorMessage = 'Invalid request. Please check your POM file and try again.';
        } else if (err.response.status === 404) {
          errorMessage = 'Project not found. Please return to the projects page and try again.';
        } else if (err.response.data && err.response.data.message) {
          errorMessage = err.response.data.message;
        }

        console.error('Server error response:', err.response.data);
      } else if (err.request) {
        // Request was made but no response received
        errorMessage = 'No response from server. Please check your connection and try again.';
      }

      setError(errorMessage);
      setActiveStep(1); // Return to previous step on error
    } finally {
      setLoading(false);
    }
  };

  const handleRemoveFile = () => {
    setPomFile(null);
    setPomContent('');
    // Clear the directory path when removing the file
    setPomDirectoryPath('');
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 10 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <IconButton 
          onClick={() => navigate(`/projects/${projectId}`)} 
          sx={{ mr: 1 }}
        >
          <ArrowBackIcon />
        </IconButton>
        <Typography variant="h4" component="h1">
          New Dependency Analysis
        </Typography>
      </Box>

      <Paper sx={{ p: 3, mb: 4 }}>
        <Typography variant="subtitle1" gutterBottom>
          Project: <strong>{project.name}</strong>
        </Typography>
        <Typography variant="body2" color="text.secondary" gutterBottom>
          {project.description}
        </Typography>
        <Chip 
          icon={<CodeIcon />} 
          label="MAVEN" 
          size="small" 
          sx={{ mt: 1 }} 
        />
      </Paper>

      <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>

      <Paper sx={{ p: 3 }}>
        {activeStep === 0 && (
          <>
            <Typography variant="h6" gutterBottom>
              Select Input Method
            </Typography>
            <Typography variant="body2" color="text.secondary" gutterBottom sx={{ mb: 3 }}>
              Choose how you want to provide your project's POM file for analysis.
            </Typography>

            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Card 
                  variant="outlined"
                  sx={{ 
                    cursor: 'pointer',
                    height: '100%',
                    bgcolor: inputMethod === 'file' ? 'rgba(33, 150, 243, 0.08)' : 'inherit',
                    border: inputMethod === 'file' ? '1px solid #2196f3' : '1px solid rgba(0, 0, 0, 0.12)',
                  }}
                  onClick={() => setInputMethod('file')}
                >
                  <CardContent sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', p: 4 }}>
                    <CloudUploadIcon fontSize="large" color="primary" sx={{ mb: 2 }} />
                    <Typography variant="h6" gutterBottom>
                      Upload POM File
                    </Typography>
                    <Typography variant="body2" color="text.secondary" align="center">
                      Upload your project's pom.xml file from your computer.
                    </Typography>

                    {inputMethod === 'file' && (
                      <Box sx={{ mt: 3, width: '100%' }}>
                        <input
                          ref={fileInputRef}
                          type="file"
                          accept=".xml"
                          style={{ display: 'none' }}
                          onChange={handleFileUpload}
                        />
                        <Button
                          variant="outlined"
                          startIcon={<DescriptionIcon />}
                          onClick={handleFileClick}
                          fullWidth
                        >
                          {pomFile ? pomFile.name : 'Select File'}
                        </Button>
                        {pomFile && (
                          <Box sx={{ mt: 2, display: 'flex', alignItems: 'center' }}>
                            <Chip 
                              label={pomFile.name} 
                              onDelete={handleRemoveFile} 
                            />
                            <Typography variant="caption" color="text.secondary" sx={{ ml: 1 }}>
                              {(pomFile.size / 1024).toFixed(2)} KB
                            </Typography>
                          </Box>
                        )}
                        {inputMethod === 'file' && !pomFile && (
                          <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mt: 1 }}>
                            Tip: If you want to use BOM resolution, uploading from the project directory is recommended.
                          </Typography>
                        )}
                      </Box>
                    )}
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} md={6}>
                <Card 
                  variant="outlined"
                  sx={{ 
                    cursor: 'pointer',
                    height: '100%',
                    bgcolor: inputMethod === 'manual' ? 'rgba(33, 150, 243, 0.08)' : 'inherit',
                    border: inputMethod === 'manual' ? '1px solid #2196f3' : '1px solid rgba(0, 0, 0, 0.12)',
                  }}
                  onClick={() => setInputMethod('manual')}
                >
                  <CardContent sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', p: 4 }}>
                    <CodeIcon fontSize="large" color="primary" sx={{ mb: 2 }} />
                    <Typography variant="h6" gutterBottom>
                      Paste POM Content
                    </Typography>
                    <Typography variant="body2" color="text.secondary" align="center">
                      Manually paste the content of your pom.xml file.
                    </Typography>

                    {inputMethod === 'manual' && (
                      <TextField
                        multiline
                        rows={8}
                        fullWidth
                        placeholder="Paste your pom.xml content here..."
                        value={pomContent}
                        onChange={(e) => setPomContent(e.target.value)}
                        sx={{ mt: 3 }}
                      />
                    )}
                  </CardContent>
                </Card>
              </Grid>
            </Grid>
          </>
        )}

        {activeStep === 1 && (
          <>
            <Typography variant="h6" gutterBottom>
              Configure Analysis
            </Typography>
            <Typography variant="body2" color="text.secondary" gutterBottom sx={{ mb: 3 }}>
              Customize your dependency analysis options.
            </Typography>

            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Card variant="outlined">
                  <CardContent>
                    <Typography variant="subtitle1" gutterBottom>
                      Analysis Options
                    </Typography>
                    <Divider sx={{ mb: 2 }} />

                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                      <Typography variant="body2">Check for vulnerabilities</Typography>
                      <Button 
                        size="small" 
                        variant={analysisOptions.checkVulnerabilities ? "contained" : "outlined"} 
                        color={analysisOptions.checkVulnerabilities ? "primary" : "inherit"}
                        onClick={() => handleOptionChange('checkVulnerabilities')}
                      >
                        {analysisOptions.checkVulnerabilities ? "Enabled" : "Disabled"}
                      </Button>
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                      <Typography variant="body2">Check licenses</Typography>
                      <Button 
                        size="small" 
                        variant={analysisOptions.checkLicenses ? "contained" : "outlined"} 
                        color={analysisOptions.checkLicenses ? "primary" : "inherit"}
                        onClick={() => handleOptionChange('checkLicenses')}
                      >
                        {analysisOptions.checkLicenses ? "Enabled" : "Disabled"}
                      </Button>
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                      <Typography variant="body2">Generate report</Typography>
                      <Button 
                        size="small" 
                        variant={analysisOptions.generateReport ? "contained" : "outlined"} 
                        color={analysisOptions.generateReport ? "primary" : "inherit"}
                        onClick={() => handleOptionChange('generateReport')}
                      >
                        {analysisOptions.generateReport ? "Enabled" : "Disabled"}
                      </Button>
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                      <Typography variant="body2">Include transitive dependencies</Typography>
                      <Button 
                        size="small" 
                        variant={analysisOptions.includeTransitive ? "contained" : "outlined"} 
                        color={analysisOptions.includeTransitive ? "primary" : "inherit"}
                        onClick={() => handleOptionChange('includeTransitive')}
                      >
                        {analysisOptions.includeTransitive ? "Enabled" : "Disabled"}
                      </Button>
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'space-between' }}>
                      <Typography variant="body2">Notify on completion</Typography>
                      <Button 
                        size="small" 
                        variant={analysisOptions.notifyOnCompletion ? "contained" : "outlined"} 
                        color={analysisOptions.notifyOnCompletion ? "primary" : "inherit"}
                        onClick={() => handleOptionChange('notifyOnCompletion')}
                      >
                        {analysisOptions.notifyOnCompletion ? "Enabled" : "Disabled"}
                      </Button>
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1, mt: 1 }}>
                      <Box>
                        <Typography variant="body2">Resolve BOM-managed versions</Typography>
                        <Typography variant="caption" color="text.secondary">
                          Uses Maven to resolve actual versions of dependencies managed by BOMs
                        </Typography>
                      </Box>
                      <Button 
                        size="small" 
                        variant={analysisOptions.resolveBomVersions ? "contained" : "outlined"} 
                        color={analysisOptions.resolveBomVersions ? "primary" : "inherit"}
                        onClick={() => handleOptionChange('resolveBomVersions')}
                        disabled={inputMethod === 'file' && pomDirectoryPath !== ''}
                      >
                        {analysisOptions.resolveBomVersions ? "Enabled" : "Disabled"}
                      </Button>
                    </Box>

                    {analysisOptions.resolveBomVersions && (
                      <Box sx={{ mt: 2 }}>
                        <Typography variant="body2" gutterBottom>
                          POM Directory Path
                        </Typography>
                        <TextField
                          fullWidth
                          size="small"
                          placeholder="Enter path to directory containing pom.xml"
                          value={pomDirectoryPath}
                          onChange={(e) => setPomDirectoryPath(e.target.value)}
                          helperText={inputMethod === 'file' && pomDirectoryPath ? "Path detected automatically from uploaded file. You can modify it if needed." : "Absolute path to directory where Maven commands can be executed (e.g., /path/to/your/maven/project)"}
                          disabled={inputMethod === 'file' && pomDirectoryPath !== ''}
                        />
                        <Alert severity="info" sx={{ mt: 2, fontSize: '0.8rem' }}>
                          {inputMethod === 'manual' ? 
                            "This feature uses Maven's help:evaluate plugin to resolve actual versions of dependencies managed by BOMs. The directory must contain a valid pom.xml file and have Maven installed." :
                            "When uploading a pom.xml file, we'll try to detect its path automatically for BOM resolution. If detection fails or you paste POM content, you'll need to provide the path manually."}
                        </Alert>
                      </Box>
                    )}
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} md={6}>
                {(inputMethod === 'file' && pomFile) || (inputMethod === 'manual' && pomContent) ? (
                  <Card variant="outlined">
                    <CardContent>
                      <Typography variant="subtitle1" gutterBottom>
                        POM File Preview
                      </Typography>
                      <Divider sx={{ mb: 2 }} />

                      <Box sx={{ 
                        maxHeight: 250, 
                        overflow: 'auto',
                        bgcolor: 'rgba(0, 0, 0, 0.03)',
                        borderRadius: 1,
                        p: 2,
                        fontFamily: 'monospace',
                        fontSize: '0.875rem',
                        whiteSpace: 'pre-wrap',
                      }}>
                        {pomContent ? pomContent.substring(0, 2000) + (pomContent.length > 2000 ? '...' : '') : 'No content to preview'}
                      </Box>
                    </CardContent>
                  </Card>
                ) : (
                  <Card variant="outlined">
                    <CardContent sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', height: '100%', p: 4 }}>
                      <Alert severity="info" sx={{ width: '100%' }}>
                        Please upload or paste your POM file content to see a preview.
                      </Alert>
                    </CardContent>
                  </Card>
                )}
              </Grid>
            </Grid>
          </>
        )}

        {activeStep === 2 && (
          <>
            <Typography variant="h6" gutterBottom align="center">
              {loading ? 'Analysis in Progress' : 'Review & Start Analysis'}
            </Typography>
            <Typography variant="body2" color="text.secondary" align="center" sx={{ mb: 2 }}>
              {!loading 
                ? 'Review your settings and click "Start Analysis" when ready.' 
                : analysisOptions.checkVulnerabilities 
                  ? 'Processing POM file and initializing vulnerability scanning. You will be redirected to the results page where scanning will continue in the background.' 
                  : 'Processing POM file and analyzing dependencies...'}
            </Typography>

            {loading && analysisOptions.checkVulnerabilities && (
              <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', mb: 4 }}>
                <CircularProgress size={60} sx={{ mb: 2 }} />
                <Box sx={{ width: '100%', maxWidth: 400 }}>
                  <Box sx={{ width: '100%', bgcolor: 'background.paper', borderRadius: 1, p: 1, mb: 1 }}>
                    <Box sx={{ height: 10, width: '100%', bgcolor: 'primary.lighter', borderRadius: 5, position: 'relative', overflow: 'hidden' }}>
                      <Box
                        sx={{
                          position: 'absolute',
                          left: 0,
                          top: 0,
                          height: '100%',
                          bgcolor: 'primary.main',
                          width: '30%',
                          borderRadius: 5,
                          animation: 'pulse 1.5s infinite ease-in-out',
                          '@keyframes pulse': {
                            '0%': { opacity: 0.6 },
                            '50%': { opacity: 1 },
                            '100%': { opacity: 0.6 }
                          }
                        }}
                      />
                    </Box>
                  </Box>
                  <Typography variant="caption" color="text.secondary" align="center" sx={{ display: 'block' }}>
                    <Box component="span" sx={{ fontWeight: 'bold', color: 'primary.main' }}>Security scanning in progress</Box> - This may take a moment as we check each dependency for vulnerabilities
                  </Typography>
                </Box>
              </Box>
            )}

            {loading && !analysisOptions.checkVulnerabilities && (
              <Box sx={{ display: 'flex', justifyContent: 'center', mb: 4 }}>
                <CircularProgress />
              </Box>
            )}

            {!loading && (
              <>
                <Typography variant="h6" gutterBottom>
                  Review & Start Analysis
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom sx={{ mb: 3 }}>
                  Review your settings and start the dependency analysis.
                </Typography>
              </>
            )}

            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Card variant="outlined">
                  <CardContent>
                    <Typography variant="subtitle1" gutterBottom>
                      Analysis Summary
                    </Typography>
                    <Divider sx={{ mb: 2 }} />

                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body2" color="text.secondary">Project:</Typography>
                      <Typography variant="body1">{project.name}</Typography>
                    </Box>

                    <Box sx={{ mb: 2 }}>
                      <Typography variant="body2" color="text.secondary">Input Method:</Typography>
                      <Typography variant="body1">
                        {inputMethod === 'file' ? 'Uploaded File' : 'Manual Input'}
                        {inputMethod === 'file' && pomFile && (
                          <Typography variant="caption" sx={{ display: 'block' }}>
                            {pomFile.name} ({(pomFile.size / 1024).toFixed(2)} KB)
                          </Typography>
                        )}
                      </Typography>
                    </Box>

                    <Box>
                      <Typography variant="body2" color="text.secondary" gutterBottom>
                        Analysis Options:
                      </Typography>
                      <Box sx={{ pl: 2 }}>
                        <Typography variant="body2">
                          • Vulnerability Check: <strong>{analysisOptions.checkVulnerabilities ? 'Enabled' : 'Disabled'}</strong>
                          {analysisOptions.checkVulnerabilities && (
                            <Typography variant="caption" display="block" color="text.secondary" sx={{ mt: 0.5 }}>
                              Real-time security checks will be performed for each dependency
                            </Typography>
                          )}
                        </Typography>
                        <Typography variant="body2">
                          • License Check: <strong>{analysisOptions.checkLicenses ? 'Enabled' : 'Disabled'}</strong>
                        </Typography>
                        <Typography variant="body2">
                          • Report Generation: <strong>{analysisOptions.generateReport ? 'Enabled' : 'Disabled'}</strong>
                        </Typography>
                        <Typography variant="body2">
                          • Transitive Dependencies: <strong>{analysisOptions.includeTransitive ? 'Included' : 'Excluded'}</strong>
                        </Typography>
                        <Typography variant="body2">
                          • Completion Notification: <strong>{analysisOptions.notifyOnCompletion ? 'Enabled' : 'Disabled'}</strong>
                        </Typography>
                      </Box>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>

              <Grid item xs={12} md={6}>
                <Card variant="outlined">
                  <CardContent sx={{ display: 'flex', flexDirection: 'column', height: '100%', justifyContent: 'space-between' }}>
                    <Box>
                      <Typography variant="subtitle1" gutterBottom>
                        Start Analysis
                      </Typography>
                      <Divider sx={{ mb: 2 }} />

                      <Typography variant="body2" paragraph>
                        When you click "Start Analysis", the system will:
                      </Typography>
                      <Box sx={{ pl: 2, mb: 3 }}>
                        <Typography variant="body2" gutterBottom>
                          1. Parse your POM file
                        </Typography>
                        <Typography variant="body2" gutterBottom>
                          2. Analyze all dependencies
                        </Typography>
                        <Typography variant="body2" gutterBottom>
                          3. Check for updates, vulnerabilities, and license issues
                        </Typography>
                        <Typography variant="body2" gutterBottom>
                          4. Generate reports and visualizations
                        </Typography>
                      </Box>

                      {error && (
                        <Alert severity="error" sx={{ mb: 3 }}>
                          {error}
                        </Alert>
                      )}
                    </Box>

                    <Box sx={{ display: 'flex', justifyContent: 'flex-end', mt: 3 }}>
                      <Button
                        variant="contained"
                        color="primary"
                        size="large"
                        onClick={submitAnalysis}
                        disabled={loading || !((inputMethod === 'file' && pomFile) || (inputMethod === 'manual' && pomContent))}
                        fullWidth
                        sx={{ 
                          py: 1.5,
                          bgcolor: 'primary.main',
                          '&:hover': { bgcolor: 'primary.dark' }
                        }}
                      >
                        {loading ? (
                          <Box sx={{ display: 'flex', alignItems: 'center' }}>
                            <CircularProgress size={24} color="inherit" sx={{ mr: 1 }} />
                            Starting Analysis...
                          </Box>
                        ) : (
                          'Start Analysis'
                        )}
                      </Button>
                    </Box>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>
          </>
        )}

        <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
          <Button
            disabled={activeStep === 0}
            onClick={handleBack}
          >
            Back
          </Button>
          <Button
            variant="contained"
            onClick={handleNext}
            disabled={
              activeStep === steps.length - 1 ||
              (activeStep === 0 && !(
                (inputMethod === 'file' && pomFile) || 
                (inputMethod === 'manual' && pomContent)
              ))
            }
          >
            Next
          </Button>
        </Box>
      </Paper>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar(prev => ({ ...prev, open: false }))}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert 
          onClose={() => setSnackbar(prev => ({ ...prev, open: false }))} 
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </>
  );
};

export default NewAnalysis;
