import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Typography,
  Paper,
  Box,
  Grid,
  Tabs,
  Tab,
  Card,
  CardContent,
  Divider,
  CircularProgress,
  Button,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Tooltip,
  TextField,
  InputAdornment,
  Snackbar,
  Alert,
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import DownloadIcon from '@mui/icons-material/Download';
import ShareIcon from '@mui/icons-material/Share';
import SearchIcon from '@mui/icons-material/Search';
import WarningIcon from '@mui/icons-material/Warning';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import UpdateIcon from '@mui/icons-material/Update';
import InfoIcon from '@mui/icons-material/Info';
import LockIcon from '@mui/icons-material/Lock';
import api from '../services/api';

// Sample chart component (to be replaced with a real chart library)
const SampleChart = ({ title, type }) => {
  return (
    <Box
      sx={{
        height: 250,
        bgcolor: 'rgba(33, 150, 243, 0.1)',
        borderRadius: 1,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column',
        p: 2,
      }}
    >
      <Typography variant="subtitle1" gutterBottom>
        {title}
      </Typography>
      <Typography color="text.secondary">
        {type} Chart
      </Typography>
    </Box>
  );
};

// Tab panel component for tab content
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`analysis-tabpanel-${index}`}
      aria-labelledby={`analysis-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

const AnalysisDetail = () => {
  const params = useParams();
  const analysisId = params.id;
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [vulnerabilitiesLoading, setVulnerabilitiesLoading] = useState(true);
  const [analysis, setAnalysis] = useState(null);
  const [tabValue, setTabValue] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');
  const [dependencies, setDependencies] = useState([]);
  const [error, setError] = useState(null);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'info'
  });

  // Poll for vulnerability updates
  const pollForVulnerabilityUpdates = async () => {
    try {
      console.log(`Starting vulnerability polling for analysis ID: ${analysisId}`);

      // Set an interval to check for vulnerability updates
      const intervalId = setInterval(async () => {
        try {
          // Use the specific vulnerability status endpoint for more efficient polling
          console.log(`Polling vulnerability status for analysis ID: ${analysisId}`);
          const response = await api.dependencyAnalysis.getVulnerabilityStatus(analysisId);

          console.log(`Received vulnerability status for analysis ID: ${analysisId}`, {
            status: response.data.vulnerabilityCheckStatus,
            dependencies: response.data.dependencies?.length || 0
          });

          // Calculate the progress percentage for the UI
          const totalDeps = response.data.dependencies?.length || 0;
          const processedDeps = response.data.dependencies?.filter(d => d.vulnerableCount !== null).length || 0;
          const progressPercent = totalDeps > 0 ? Math.round((processedDeps / totalDeps) * 100) : 0;

          // Update the analysis with the latest data
          const totalVulnerabilities = response.data.dependencies 
            ? response.data.dependencies.reduce((total, dep) => total + (dep.vulnerableCount || 0), 0)
            : 0;

          setAnalysis({
            ...response.data,
            vulnerableCount: totalVulnerabilities,
            scanProgress: progressPercent,
            processedDependencies: processedDeps,
            totalDependencies: totalDeps,
            status: response.data.vulnerabilityCheckStatus === 'IN_PROGRESS' ? 'in_progress' : 
                   response.data.vulnerabilityCheckStatus === 'COMPLETED' ? 'completed' :
                   response.data.vulnerabilityCheckStatus === 'FAILED' ? 'failed' :
                   response.data.status || 'completed'
          });

          if (response.data.dependencies) {
            setDependencies(response.data.dependencies);
          }

          // If vulnerability checking is complete, stop polling
          if (response.data.vulnerabilityCheckStatus !== 'IN_PROGRESS') {
            console.log(`Vulnerability check completed for analysis ID: ${analysisId}. Status: ${response.data.vulnerabilityCheckStatus}`);
            clearInterval(intervalId);
            setVulnerabilitiesLoading(false);

            // Force a refresh of the data to ensure we have the final state
            setTimeout(async () => {
              try {
                const finalResponse = await api.dependencyAnalysis.getById(analysisId);

                // Update one last time with complete data
                const finalVulnerabilities = finalResponse.data.dependencies 
                  ? finalResponse.data.dependencies.reduce((total, dep) => total + (dep.vulnerableCount || 0), 0)
                  : 0;

                setAnalysis({
                  ...finalResponse.data,
                  vulnerableCount: finalVulnerabilities,
                  status: finalResponse.data.vulnerabilityCheckStatus === 'IN_PROGRESS' ? 'in_progress' : 
                         finalResponse.data.vulnerabilityCheckStatus === 'COMPLETED' ? 'completed' :
                         finalResponse.data.vulnerabilityCheckStatus === 'FAILED' ? 'failed' :
                         finalResponse.data.status || 'completed'
                });

                if (finalResponse.data.dependencies) {
                  setDependencies(finalResponse.data.dependencies);
                }

                console.log(`Final refresh completed for analysis ID: ${analysisId}`);

                // Success notification
                setSnackbar({
                  open: true,
                  message: 'Vulnerability scanning completed',
                  severity: 'success'
                });
              } catch (refreshError) {
                console.error('Error refreshing final data:', refreshError);
                setSnackbar({
                  open: true,
                  message: 'Error loading final vulnerability data. You can try refreshing manually.',
                  severity: 'warning'
                });
              }
            }, 1000);
          }
        } catch (pollingError) {
          console.error('Error during polling interval:', pollingError);

          // Show a warning but don't stop polling yet
          setSnackbar({
            open: true,
            message: 'Error checking vulnerability status. Retrying...',
            severity: 'warning'
          });
        }
      }, 3000); // Check every 3 seconds

      // Clean up the interval when the component unmounts
      return () => {
        console.log(`Cleaning up polling interval for analysis ID: ${analysisId}`);
        clearInterval(intervalId);
      };
    } catch (error) {
      console.error('Error setting up vulnerability polling:', error);
      setVulnerabilitiesLoading(false);
      setSnackbar({
        open: true,
        message: 'Could not set up vulnerability status monitoring. Try refreshing the page.',
        severity: 'error'
      });
    }
  };

  useEffect(() => {
    // Log the ID to verify it's available
    console.log("Current analysis ID from params:", analysisId);

    // Don't proceed if analysisId is undefined or null
    if (!analysisId) {
      console.error("Analysis ID is missing from URL parameters");
      setError("Missing analysis ID. Please select a valid analysis.");
      setLoading(false);
      return;
    }

    // Fetch actual analysis data from the API
    const fetchAnalysisData = async () => {
      setLoading(true);
      try {
        // Get analysis details
        console.log(`Fetching analysis data for ID: ${analysisId}`);
        const response = await api.dependencyAnalysis.getById(analysisId);
        console.log('Analysis data received:', response.data);

        // Calculate total vulnerabilities for the header stats
        const totalVulnerabilities = response.data.dependencies 
          ? response.data.dependencies.reduce((total, dep) => total + (dep.vulnerableCount || 0), 0)
          : 0;

        // Combine the analysis data with calculated values
        setAnalysis({
          ...response.data,
          vulnerableCount: totalVulnerabilities,
          // Map the vulnerabilityCheckStatus to a UI-friendly status
          status: response.data.vulnerabilityCheckStatus === 'IN_PROGRESS' ? 'in_progress' : 
                 response.data.vulnerabilityCheckStatus === 'COMPLETED' ? 'completed' :
                 response.data.vulnerabilityCheckStatus === 'FAILED' ? 'failed' :
                 response.data.status || 'completed'
        });

        // If the analysis contains dependencies, set them
        if (response.data.dependencies) {
          setDependencies(response.data.dependencies);
          console.log(`Loaded ${response.data.dependencies.length} dependencies`);
        } else {
          console.log("No dependencies found in the response data");
        }

        // If vulnerability checking is still in progress, start a polling mechanism
        if (response.data.vulnerabilityCheckStatus === 'IN_PROGRESS') {
          pollForVulnerabilityUpdates();
        } else {
          setVulnerabilitiesLoading(false);
        }
      } catch (error) {
        console.error('Error fetching analysis:', error);
        setError(`Failed to load analysis data: ${error.message || 'Unknown error'}`);
        setSnackbar({
          open: true,
          message: `Error loading analysis: ${error.message || 'Unknown error'}`,
          severity: 'error'
        });

        // Set mock data for development/fallback
        const mockAnalysis = {
          id: parseInt(analysisId),
          projectId: 1,
          projectName: 'Error Loading Project',
          date: new Date().toISOString().split('T')[0],
          status: 'error',
          totalDependencies: 0,
          outdatedDependencies: 0,
          vulnerabilities: 0,
          licenseIssues: 0,
        };
        setAnalysis(mockAnalysis);
        setDependencies([]);
        setVulnerabilitiesLoading(false);
      } finally {
        setLoading(false);
      }
    };

    fetchAnalysisData();
  }, [analysisId]);

  const handleTabChange = (event, newValue) => {
    setTabValue(newValue);
  };

  // Filtering dependencies based on search query
  const filteredDependencies = dependencies.filter(dep => {
    const fullName = `${dep.groupId}:${dep.artifactId}:${dep.currentVersion}`.toLowerCase();
    return fullName.includes(searchQuery.toLowerCase());
  });

  // Collecting all vulnerabilities from dependencies for the vulnerabilities tab
  const allVulnerabilities = React.useMemo(() => {
    const vulnerabilities = [];
    dependencies.forEach(dep => {
      if (dep.vulnerabilities && dep.vulnerabilities.length > 0) {
        dep.vulnerabilities.forEach(vuln => {
          vulnerabilities.push({
            ...vuln,
            dependency: `${dep.groupId}:${dep.artifactId}:${dep.currentVersion}`
          });
        });
      }
    });
    return vulnerabilities;
  }, [dependencies]);

  // Add a refresh function
  const refreshVulnerabilityStatus = async () => {
    try {
      // Show loading state immediately for better UX
      setSnackbar({
        open: true,
        message: 'Refreshing vulnerability status...',
        severity: 'info'
      });

      setVulnerabilitiesLoading(true);

      // Add a small delay to ensure the UI reflects the loading state
      await new Promise(resolve => setTimeout(resolve, 300));

      // Fetch the latest analysis data
      const response = await api.dependencyAnalysis.getById(analysisId);

      // Calculate the progress percentage for the UI
      const totalDeps = response.data.dependencies?.length || 0;
      const processedDeps = response.data.dependencies?.filter(d => d.vulnerableCount !== null).length || 0;
      const progressPercent = totalDeps > 0 ? Math.round((processedDeps / totalDeps) * 100) : 0;

      // Update the analysis with the latest data
      const totalVulnerabilities = response.data.dependencies 
        ? response.data.dependencies.reduce((total, dep) => total + (dep.vulnerableCount || 0), 0)
        : 0;

      setAnalysis({
        ...response.data,
        vulnerableCount: totalVulnerabilities,
        scanProgress: progressPercent,
        processedDependencies: processedDeps,
        totalDependencies: totalDeps,
        status: response.data.vulnerabilityCheckStatus === 'IN_PROGRESS' ? 'in_progress' : 
                response.data.vulnerabilityCheckStatus === 'COMPLETED' ? 'completed' :
                response.data.vulnerabilityCheckStatus === 'FAILED' ? 'failed' :
                response.data.status || 'completed'
      });

      if (response.data.dependencies) {
        setDependencies(response.data.dependencies);
      }

      // Check if vulnerability processing is still in progress
      if (response.data.vulnerabilityCheckStatus === 'IN_PROGRESS') {
        // Start polling if still in progress
        pollForVulnerabilityUpdates();

        setSnackbar({
          open: true,
          message: `Vulnerability scan is ${progressPercent}% complete. Continuing to monitor progress.`,
          severity: 'info'
        });
      } else {
        setVulnerabilitiesLoading(false);

        setSnackbar({
          open: true,
          message: 'Vulnerability scan is complete!',
          severity: 'success'
        });
      }
    } catch (error) {
      console.error('Error refreshing vulnerability status:', error);

      // Provide specific error feedback based on the type of error
      let errorMessage = 'Failed to refresh analysis data. Please try again.';

      if (error.response) {
        if (error.response.status === 404) {
          errorMessage = 'Analysis not found. It may have been deleted.';
        } else if (error.response.data && error.response.data.message) {
          errorMessage = error.response.data.message;
        }
      } else if (error.request) {
        errorMessage = 'Server not responding. Please check your connection.';
      }

      setSnackbar({
        open: true,
        message: errorMessage,
        severity: 'error'
      });

      // Still stop the loading state even on error
      setVulnerabilitiesLoading(false);
    }
  };

  const handleCloseSnackbar = () => {
    setSnackbar({ ...snackbar, open: false });
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', mt: 10 }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 3 }}>
        <IconButton 
          onClick={() => navigate(-1)} 
          sx={{ mr: 1 }}
        >
          <ArrowBackIcon />
        </IconButton>
        <Typography variant="h4" component="h1" sx={{ flexGrow: 1 }}>
          Analysis Details
        </Typography>
        <Button 
          variant="outlined" 
          startIcon={<DownloadIcon />}
          sx={{ mr: 1 }}
        >
          Export
        </Button>
        <Button
          variant="outlined"
          startIcon={<ShareIcon />}
        >
          Share
        </Button>
      </Box>

      <Paper sx={{ p: 3, mb: 4 }}>
        <Grid container spacing={2}>
          <Grid item xs={12} md={8}>
            <Typography variant="h6">
              Project: {analysis.projectName}
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Analysis completed on {analysis.date}
            </Typography>
          </Grid>
          <Grid item xs={12} md={4} sx={{ display: 'flex', justifyContent: { xs: 'flex-start', md: 'flex-end' } }}>
            <Box>
              <Chip 
                label={
                  analysis.vulnerabilityCheckStatus === 'IN_PROGRESS' ? 'In Progress' :
                  analysis.status === 'completed' || analysis.status === 'COMPLETED' ? 'Completed' : 
                  analysis.status === 'FAILED' ? 'Failed' : 
                  'In Progress'
                } 
                color={
                  analysis.vulnerabilityCheckStatus === 'IN_PROGRESS' ? 'primary' :
                  analysis.status === 'completed' || analysis.status === 'COMPLETED' ? 'success' : 
                  analysis.status === 'FAILED' ? 'error' : 
                  'primary'
                }
                sx={{ mb: 1 }}
              />
              <Typography variant="body2" color="text.secondary">
                ID: {analysis.id}
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </Paper>

      {/* Summary Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={6} sm={3}>
          <Paper
            elevation={0}
            sx={{
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              bgcolor: 'rgba(33, 150, 243, 0.1)',
              borderRadius: 2,
            }}
          >
            <Typography color="text.secondary" variant="body2">
              Total Dependencies
            </Typography>
            <Typography variant="h5" sx={{ mt: 1 }}>
              {analysis.totalDependencies}
            </Typography>
          </Paper>
        </Grid>

        <Grid item xs={6} sm={3}>
          <Paper
            elevation={0}
            sx={{
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              bgcolor: 'rgba(255, 152, 0, 0.1)',
              borderRadius: 2,
            }}
          >
            <Typography color="text.secondary" variant="body2">
              Outdated
            </Typography>
            <Typography variant="h5" sx={{ mt: 1, color: 'warning.main' }}>
              {analysis.outdatedDependencies}
            </Typography>
          </Paper>
        </Grid>

        <Grid item xs={6} sm={3}>
          <Paper
            elevation={0}
            sx={{
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              bgcolor: 'rgba(239, 83, 80, 0.1)',
              borderRadius: 2,
            }}
          >
            <Typography color="text.secondary" variant="body2">
              Vulnerabilities
            </Typography>
            <Typography variant="h5" sx={{ mt: 1, color: 'error.main' }}>
              {analysis.vulnerableCount || 0}
            </Typography>
          </Paper>
        </Grid>

        <Grid item xs={6} sm={3}>
          <Paper
            elevation={0}
            sx={{
              p: 2,
              display: 'flex',
              flexDirection: 'column',
              bgcolor: 'rgba(156, 39, 176, 0.1)',
              borderRadius: 2,
            }}
          >
            <Typography color="text.secondary" variant="body2">
              License Issues
            </Typography>
            <Typography variant="h5" sx={{ mt: 1, color: '#9c27b0' }}>
              {analysis.licenseIssues}
            </Typography>
          </Paper>
        </Grid>
      </Grid>

      <Paper sx={{ mb: 4 }}>
        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
          <Tabs 
            value={tabValue} 
            onChange={handleTabChange} 
            aria-label="analysis tabs"
            variant="scrollable"
            scrollButtons="auto"
          >
            <Tab label="Overview" />
            <Tab label="Dependencies" />
            <Tab label="Charts" />
            <Tab label="Vulnerabilities" />
            <Tab label="Licenses" />
          </Tabs>
        </Box>

        <TabPanel value={tabValue} index={0}>
          <Typography variant="h6" gutterBottom>
            Analysis Overview
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <SampleChart title="Dependency Status" type="Pie" />
            </Grid>
            <Grid item xs={12} md={6}>
              <SampleChart title="Security Overview" type="Bar" />
            </Grid>
          </Grid>

          <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>
            Summary
          </Typography>
          <Typography variant="body1" paragraph>
            This analysis found {analysis.outdatedDependencies} outdated dependencies, {analysis.vulnerableCount || 0} security vulnerabilities, and {analysis.licenseIssues} license compliance issues.
          </Typography>
          <Typography variant="body1" paragraph>
            It's recommended to update the dependencies to their latest versions to resolve security issues and take advantage of the latest features and bug fixes.
          </Typography>

          <Box sx={{ mt: 2 }}>
            <Button variant="contained">Generate Full Report</Button>
          </Box>
        </TabPanel>

        <TabPanel value={tabValue} index={1}>
          <Box sx={{ mb: 3 }}>
            <TextField
              placeholder="Search dependencies..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              size="small"
              sx={{ width: '40%' }}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <SearchIcon />
                  </InputAdornment>
                ),
              }}
            />
          </Box>

          <TableContainer component={Paper} variant="outlined">
            <Table size="small">
              <TableHead>
                <TableRow>
                  <TableCell>Group ID</TableCell>
                  <TableCell>Artifact ID</TableCell>
                  <TableCell>Current Version</TableCell>
                  <TableCell>Latest Version</TableCell>
                  <TableCell>Scope</TableCell>
                  <TableCell>License</TableCell>
                  <TableCell>Status</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {filteredDependencies.map((dependency) => (
                  <TableRow key={dependency.id} hover>
                    <TableCell>{dependency.groupId}</TableCell>
                    <TableCell>{dependency.artifactId}</TableCell>
                    <TableCell>{dependency.currentVersion}</TableCell>
                    <TableCell>
                      {dependency.latestVersion ? (
                        dependency.isOutdated ? (
                          <Typography color="warning.main" fontWeight="medium">
                            {dependency.latestVersion} ({dependency.versionsBehind} versions behind)
                          </Typography>
                        ) : (
                          dependency.latestVersion
                        )
                      ) : (
                        <Typography color="text.secondary" fontStyle="italic">
                          Unknown
                        </Typography>
                      )}
                    </TableCell>
                    <TableCell>
                      <Chip 
                        label={dependency.scope || 'compile'} 
                        size="small" 
                        color={dependency.scope === 'test' ? 'secondary' : 
                               dependency.scope === 'provided' ? 'info' : 'default'}
                        variant="outlined"
                        sx={{ fontWeight: 'normal' }}
                      />
                    </TableCell>
                    <TableCell>
                      {dependency.license || 'Unknown'}
                    </TableCell>
                    <TableCell>
                      <Chip 
                        label={dependency.status || (
                          dependency.isVulnerable ? 'Vulnerable' : 
                          dependency.isOutdated ? 'Outdated' :
                          dependency.latestVersion ? 'Up-to-date' : 'Unknown'
                        )} 
                        size="small" 
                        color={
                          dependency.status === 'Vulnerable' || dependency.isVulnerable ? 'error' : 
                          dependency.status === 'Outdated' || dependency.isOutdated ? 'warning' :
                          dependency.status === 'Up-to-date' ? 'success' : 'default'
                        }
                      />
                    </TableCell>
                  </TableRow>
                ))}
                {filteredDependencies.length === 0 && (
                  <TableRow>
                    <TableCell colSpan={7} align="center">
                      No dependencies found
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </TabPanel>

        <TabPanel value={tabValue} index={2}>
          <Typography variant="h6" gutterBottom>
            Visualization Charts
          </Typography>

          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <SampleChart title="Dependency Update Status" type="Pie" />
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <SampleChart title="Vulnerabilities by Severity" type="Bar" />
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <SampleChart title="License Distribution" type="Donut" />
                </CardContent>
              </Card>
            </Grid>

            <Grid item xs={12} md={6}>
              <Card>
                <CardContent>
                  <SampleChart title="Dependencies by Scope" type="Bar" />
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </TabPanel>

        <TabPanel value={tabValue} index={3}>
          {vulnerabilitiesLoading ? (
            <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', py: 6 }}>
              <CircularProgress size={40} sx={{ mb: 2 }} />
              <Typography variant="h6" color="text.secondary">
                Scanning for vulnerabilities...
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mt: 1, mb: 3 }}>
                Security scanning is running in the background. You can navigate away from this page and come back later to see the results.
                Basic dependency information is already available in the Dependencies tab.
              </Typography>

              {analysis && analysis.vulnerabilityCheckStatus === 'IN_PROGRESS' && (
                <Box sx={{ mt: 3, width: '100%', maxWidth: 500, mx: 'auto' }}>
                  <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                    <Typography variant="body2" color="text.secondary">Scanning dependencies...</Typography>
                    <Typography variant="body2" color="primary">
                      {analysis.processedDependencies || dependencies.filter(d => d.vulnerableCount !== null).length} / {analysis.totalDependencies || dependencies.length} checked
                    </Typography>
                  </Box>

                  <Box sx={{ width: '100%', bgcolor: 'background.paper', borderRadius: 1, p: 1 }}>
                    <Box sx={{ 
                      height: 8, 
                      width: `${analysis.scanProgress || (dependencies.filter(d => d.vulnerableCount !== null).length / dependencies.length) * 100}%`, 
                      bgcolor: 'primary.main',
                      borderRadius: 5,
                      transition: 'width 0.5s ease-in-out'
                    }} />
                  </Box>

                  <Typography variant="caption" align="center" sx={{ display: 'block', mt: 1 }}>
                    {analysis.scanProgress ? `${analysis.scanProgress}% complete` : 'Initializing scan...'}
                  </Typography>

                  <Box sx={{ mt: 4, display: 'flex', justifyContent: 'center' }}>
                    <Button 
                      variant="outlined" 
                      onClick={refreshVulnerabilityStatus}
                      startIcon={<UpdateIcon />}
                    >
                      Refresh Status
                    </Button>
                  </Box>

                  <Typography variant="body2" color="text.secondary" sx={{ mt: 2, textAlign: 'center', fontStyle: 'italic' }}>
                    Tip: This process runs in the background and the page will update automatically when completed.
                  </Typography>
                </Box>
              )}
            </Box>
          ) : allVulnerabilities.length > 0 ? (
            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Dependency</TableCell>
                    <TableCell>Vulnerability</TableCell>
                    <TableCell>Severity</TableCell>
                    <TableCell>Description</TableCell>
                    <TableCell>Affected Versions</TableCell>
                    <TableCell>Fixed In</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {allVulnerabilities.map((vuln, index) => (
                    <TableRow key={`${vuln.id || index}`} hover>
                      <TableCell>{vuln.dependency}</TableCell>
                      <TableCell>{vuln.name}</TableCell>
                      <TableCell>
                        <Chip 
                          label={vuln.severity} 
                          color={
                            vuln.severity === 'CRITICAL' ? 'error' :
                            vuln.severity === 'HIGH' ? 'error' :
                            vuln.severity === 'MEDIUM' ? 'warning' :
                            'info'
                          } 
                          size="small" 
                        />
                      </TableCell>
                      <TableCell>{vuln.description}</TableCell>
                      <TableCell>{vuln.affectedVersions}</TableCell>
                      <TableCell>{vuln.fixedInVersion}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          ) : (
            <Box sx={{ p: 4, textAlign: 'center' }}>
              <Typography variant="h6" color="text.secondary">
                No vulnerabilities found
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                All dependencies appear to be secure. Continue to monitor for new vulnerabilities.
              </Typography>
            </Box>
          )}
        </TabPanel>

        <TabPanel value={tabValue} index={4}>
          <Typography variant="h6" gutterBottom>
            License Compliance
          </Typography>

          {analysis.licenseIssues > 0 ? (
            <>
              <Typography variant="body2" color="text.secondary" paragraph>
                The following dependencies have license issues that may need attention:
              </Typography>

              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Dependency</TableCell>
                      <TableCell>License</TableCell>
                      <TableCell>Issue</TableCell>
                      <TableCell>Recommendation</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    <TableRow hover>
                      <TableCell>mysql:mysql-connector-java:8.0.28</TableCell>
                      <TableCell>
                        <Chip label="GPL" size="small" />
                      </TableCell>
                      <TableCell>GPL is copyleft license and may be incompatible with commercial use</TableCell>
                      <TableCell>Consider using a commercial license or alternative database connector</TableCell>
                    </TableRow>
                    <TableRow hover>
                      <TableCell>unknown:library:1.0.0</TableCell>
                      <TableCell>
                        <Chip label="Unknown" size="small" />
                      </TableCell>
                      <TableCell>No license information available</TableCell>
                      <TableCell>Verify license information with the library author</TableCell>
                    </TableRow>
                  </TableBody>
                </Table>
              </TableContainer>
            </>
          ) : (
            <Box sx={{ p: 4, textAlign: 'center' }}>
              <CheckCircleIcon color="success" sx={{ fontSize: 48, mb: 2 }} />
              <Typography variant="h6" gutterBottom>
                No license issues found
              </Typography>
              <Typography variant="body2" color="text.secondary">
                All your dependencies have compatible licenses.
              </Typography>
            </Box>
          )}
        </TabPanel>
      </Paper>
      <Snackbar
        open={snackbar.open}
        autoHideDuration={5000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </>
  );
};

export default AnalysisDetail;
