import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { useSnackbar } from 'notistack';
import {
  Typography,
  Paper,
  Box,
  Grid,
  Tabs,
  Tab,
  Card,
  CardContent,
  Divider,
  CircularProgress,
  Button,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Tooltip,
  TextField,
  InputAdornment,
  Snackbar,
  Alert,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Switch,
  FormControlLabel,
} from '@mui/material';
import {
  ArrowBack as ArrowBackIcon,
  Download as DownloadIcon,
  Share as ShareIcon,
  Search as SearchIcon,
  Warning as WarningIcon,
  CheckCircle as CheckCircleIcon,
  Info as InfoIcon,
  Lock as LockIcon,
  MoreVert as MoreVertIcon,
  Timeline as TimelineIcon,
  ContentCopy as ContentCopyIcon,
  FileDownload as FileDownloadIcon,
  Insights as InsightsIcon,
  SaveAlt as SaveAltIcon,
} from '@mui/icons-material';
import api from '../services/api';
import { DependencyStatusChart, VulnerabilityChart, VulnerabilitySeverityChart, LicenseDistributionChart, VulnerabilityPieChart, VulnerabilityByDependencyChart } from '../components/charts';
import { formatDate } from '../utils/dateUtils';
import BomManagedVersion from '../components/BomManagedVersion';

// Sample chart component (kept for backward compatibility with other tabs)
const SampleChart = ({ title, type }) => {
  return (
    <Box
      sx={{
        height: 250,
        bgcolor: 'rgba(33, 150, 243, 0.1)',
        borderRadius: 1,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        flexDirection: 'column',
        p: 2,
      }}
    >
      <Typography variant="subtitle1" gutterBottom>
        {title}
      </Typography>
      <Typography color="text.secondary">
        {type} Chart
      </Typography>
    </Box>
  );
};

// Tab panel component for tab content
function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`analysis-tabpanel-${index}`}
      aria-labelledby={`analysis-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

const AnalysisDetail = () => {
  const params = useParams();
  const analysisId = params.id;
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [vulnerabilitiesLoading, setVulnerabilitiesLoading] = useState(true);
  const [analysis, setAnalysis] = useState(null);
  const [tabValue, setTabValue] = useState(0);
  const [searchQuery, setSearchQuery] = useState('');
  const [dependencies, setDependencies] = useState([]);
  const [error, setError] = useState(null);
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'info'
  });
  const [charts, setCharts] = useState({
    dependencyUpdates: null,
    vulnerabilities: null,
    licenses: null
